let rawScenes;

try {
  const content = items[0].json.message.content;

  if (content && typeof content === "object") {
    rawScenes = Object.entries(content)
      .filter(([key]) => key.startsWith("Scene"))
      .map(([, value]) => value); // Pull just the text content of each scene
  } else {
    throw new Error("Scene data is missing or not formatted as an object.");
  }

} catch (error) {
  throw new Error("Scene extraction failed: " + error.message);
}

const formattedOutput = rawScenes.map(sceneText => ({
  description: sceneText
}));

return formattedOutput;

