const contentSplit = $json.content.split('- SEPARATOR - DO NOT TOUCH (PUT CONTROL COPY BELOW) -')

const hookCTR = parseFloat(contentSplit[0].match(/[\d.]+(?=%)/)[0])
const adCopy = contentSplit[1]

return {
  json: {
    hookCTR,
    adCopy
  }
}

